int	ft_isalnum(int c)
{
	unsigned char	ch;

	ch = (unsigned char)c;
	if (((ch >= 'a') && (ch <= 'z')) || ((ch >= 'A') && (ch <= 'Z'))
		|| ((ch >= '0') && (ch <= '9')))
		return (1);
	return (0);
}
